<script>
function navigation() {
        return {
            active: "top",
            mobileMenuOpen: false,
            scrollWatch() {
                if (window.scrollY + 152 <= this.$refs.floorPlans.offsetTop) this.active = "top"
                if (window.scrollY + 152 >= this.$refs.floorPlans.offsetTop) this.update('floorPlans')
                if (window.scrollY + 104 >= this.$refs.amenities.offsetTop) this.update('amenities')
                if (window.scrollY + 104 >= this.$refs.community.offsetTop) this.update('community')
                if (window.scrollY + 152 >= this.$refs.gallery.offsetTop) this.update('gallery')
                if (window.scrollY + 152 >= this.$refs.testimonials.offsetTop) this.update('testimonials')
                if (window.scrollY + 152 >= this.$refs.map.offsetTop) this.update('map')
                if (window.scrollY + 152 >= this.$refs.contact.offsetTop) this.update('contact')
            },
            update(activeDiv) {
                this.active = activeDiv
            }
        }
    }
</script>
